import{d as s}from"./app.d8cb38ab.js";const n={},a=s('<h2 id="git-命令大全" tabindex="-1"><a class="header-anchor" href="#git-命令大全" aria-hidden="true">#</a> Git 命令大全</h2><h6 id="名词" tabindex="-1"><a class="header-anchor" href="#名词" aria-hidden="true">#</a> 名词</h6><ul><li><code>master</code>：默认开发分支</li><li><code>origin</code>：默认远程版本库</li><li><code>Index</code> / <code>Stage</code>：暂存区</li><li><code>Workspace</code>：工作区</li><li><code>Repository</code>：仓库区（或本地仓库）</li><li><code>Remote</code>：远程仓库</li></ul><h3 id="新建代码库" tabindex="-1"><a class="header-anchor" href="#新建代码库" aria-hidden="true">#</a> 新建代码库</h3><blockquote><p>创建一个新的 git 版本库。这个版本库的配置、存储等信息会被保存到.git 文件夹中</p></blockquote><div class="language-bash ext-sh"><pre class="shiki" style="background-color:#272822;"><code><span class="line"><span style="color:#88846F;"># 初始化当前项目</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git init</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 新建一个目录，将其初始化为Git代码库</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git init [project-name]</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 在指定目录创建一个空的 Git 仓库。运行这个命令会创建一个名为 directory，只包含 .git 子目录的空目录。</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git init --bare </span><span style="color:#F92672;">&lt;</span><span style="color:#F8F8F2;">directory</span><span style="color:#F92672;">&gt;</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 下载一个项目和它的整个代码历史</span></span>\n<span class="line"><span style="color:#88846F;"># 这个命令就是将一个版本库拷贝到另一个目录中，同时也将分支都拷贝到新的版本库中。这样就可以在新的版本库中提交到远程分支</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git clone [url]</span></span>\n<span class="line"></span></code></pre></div><h3 id="配置" tabindex="-1"><a class="header-anchor" href="#配置" aria-hidden="true">#</a> 配置</h3><blockquote><p>Git 的配置文件为 .gitconfig，它可以自用户主目录（全局配置），也可以在项目目录下（项目配置）</p></blockquote><div class="language-bash ext-sh"><pre class="shiki" style="background-color:#272822;"><code><span class="line"><span style="color:#88846F;"># 显示当前的Git配置</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git config --list</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 编辑Git配置文件</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git config -e [--global]</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 输出、设置基本的全局变量</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git config --global user.email</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git config --global user.name</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#F8F8F2;">$ git config --global user.email </span><span style="color:#E6DB74;">&quot;MyEmail@gmail.com&quot;</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git config --global user.name </span><span style="color:#E6DB74;">&quot;My Name&quot;</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 定义当前用户所有提交使用的作者邮箱。</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git config --global alias.</span><span style="color:#F92672;">&lt;</span><span style="color:#F8F8F2;">alias-name</span><span style="color:#F92672;">&gt;</span><span style="color:#F8F8F2;"> </span><span style="color:#F92672;">&lt;</span><span style="color:#F8F8F2;">git-command</span><span style="color:#F92672;">&gt;</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 为Git命令创建一个快捷方式（别名）。</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git config --system core.editor </span><span style="color:#F92672;">&lt;</span><span style="color:#F8F8F2;">editor</span><span style="color:#F92672;">&gt;</span></span>\n<span class="line"></span></code></pre></div><h3 id="帮助" tabindex="-1"><a class="header-anchor" href="#帮助" aria-hidden="true">#</a> 帮助</h3><div class="language-bash ext-sh"><pre class="shiki" style="background-color:#272822;"><code><span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 查找可用命令</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git </span><span style="color:#66D9EF;">help</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 查找所有可用命令</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git </span><span style="color:#66D9EF;">help</span><span style="color:#F8F8F2;"> -a</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 在文档当中查找特定的命令</span></span>\n<span class="line"><span style="color:#88846F;"># git help &lt;命令&gt;</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git </span><span style="color:#66D9EF;">help</span><span style="color:#F8F8F2;"> add</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git </span><span style="color:#66D9EF;">help</span><span style="color:#F8F8F2;"> commit</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git </span><span style="color:#66D9EF;">help</span><span style="color:#F8F8F2;"> init</span></span>\n<span class="line"></span></code></pre></div><h3 id="状态" tabindex="-1"><a class="header-anchor" href="#状态" aria-hidden="true">#</a> 状态</h3><blockquote><p>显示索引文件（也就是当前工作空间）和当前的头指针指向的提交的不同</p></blockquote><div class="language-bash ext-sh"><pre class="shiki" style="background-color:#272822;"><code><span class="line"><span style="color:#88846F;"># 显示分支，未跟踪文件，更改和其他不同</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git status</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 查看其他的git status的用法</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git </span><span style="color:#66D9EF;">help</span><span style="color:#F8F8F2;"> status</span></span>\n<span class="line"></span></code></pre></div><h3 id="信息" tabindex="-1"><a class="header-anchor" href="#信息" aria-hidden="true">#</a> 信息</h3><blockquote><p>获取某些文件，某些分支，某次提交等 git 信息</p></blockquote><div class="language-bash ext-sh"><pre class="shiki" style="background-color:#272822;"><code><span class="line"><span style="color:#88846F;"># 显示commit历史，以及每次commit发生变更的文件</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git log --stat</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 搜索提交历史，根据关键词</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git log -S [keyword]</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 显示某个commit之后的所有变动，每个commit占据一行</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git log [tag] HEAD --pretty=format:%s</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 显示某个commit之后的所有变动，其&quot;提交说明&quot;必须符合搜索条件</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git log [tag] HEAD --grep feature</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 显示某个文件的版本历史，包括文件改名</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git log --follow [file]</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git whatchanged [file]</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 显示指定文件相关的每一次diff</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git log -p [file]</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 显示过去5次提交</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git log -5 --pretty --oneline</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 显示所有提交过的用户，按提交次数排序</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git shortlog -sn</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 显示指定文件是什么人在什么时间修改过</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git blame [file]</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 显示暂存区和工作区的差异</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git diff</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 显示暂存区和上一个commit的差异</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git diff --cached [file]</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 显示工作区与当前分支最新commit之间的差异</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git diff HEAD</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 显示两次提交之间的差异</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git diff [first-branch]...[second-branch]</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 显示今天你写了多少行代码</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git diff --shortstat </span><span style="color:#E6DB74;">&quot;@{0 day ago}&quot;</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 比较暂存区和版本库差异</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git diff --staged</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 比较暂存区和版本库差异</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git diff --cached</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 仅仅比较统计信息</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git diff --stat</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 显示某次提交的元数据和内容变化</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git show [commit]</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 显示某次提交发生变化的文件</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git show --name-only [commit]</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 显示某次提交时，某个文件的内容</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git show [commit]:[filename]</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 显示当前分支的最近几次提交</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git reflog</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 查看远程分支</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git branch -r</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 创建新的分支</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git branch </span><span style="color:#F92672;">&lt;</span><span style="color:#F8F8F2;">new_branch</span><span style="color:#F92672;">&gt;</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 查看各个分支最后提交信息</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git branch -v</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 查看已经被合并到当前分支的分支</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git branch --merged</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 查看尚未被合并到当前分支的分支</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git branch --no-merged</span></span>\n<span class="line"></span></code></pre></div><h3 id="添加" tabindex="-1"><a class="header-anchor" href="#添加" aria-hidden="true">#</a> 添加</h3><blockquote><p>添加文件到当前工作空间中。如果你不使用<code>git add</code>将文件添加进去，那么这些文件也不会添加到之后的提交之中</p></blockquote><div class="language-bash ext-sh"><pre class="shiki" style="background-color:#272822;"><code><span class="line"><span style="color:#88846F;"># 添加一个文件</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git add test.js</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 添加一个子目录中的文件</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git add /path/to/file/test.js</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 支持正则表达式</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git add ./</span><span style="color:#F92672;">*</span><span style="color:#F8F8F2;">.js</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 添加指定文件到暂存区</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git add [file1] [file2] ...</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 添加指定目录到暂存区，包括子目录</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git add [dir]</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 添加当前目录的所有文件到暂存区</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git add </span><span style="color:#66D9EF;">.</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 添加每个变化前，都会要求确认</span></span>\n<span class="line"><span style="color:#88846F;"># 对于同一个文件的多处变化，可以实现分次提交</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git add -p</span></span>\n<span class="line"></span></code></pre></div><h3 id="删除" tabindex="-1"><a class="header-anchor" href="#删除" aria-hidden="true">#</a> 删除</h3><blockquote><p>rm 和上面的 add 命令相反，从工作空间中去掉某个文件</p></blockquote><div class="language-bash ext-sh"><pre class="shiki" style="background-color:#272822;"><code><span class="line"><span style="color:#88846F;"># 移除 HelloWorld.js</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git rm HelloWorld.js</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 移除子目录中的文件</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git rm /pather/to/the/file/HelloWorld.js</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 删除工作区文件，并且将这次删除放入暂存区</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git rm [file1] [file2] ...</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 停止追踪指定文件，但该文件会保留在工作区</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git rm --cached [file]</span></span>\n<span class="line"></span></code></pre></div><h3 id="分支" tabindex="-1"><a class="header-anchor" href="#分支" aria-hidden="true">#</a> 分支</h3><blockquote><p>管理分支，可以通过下列命令对分支进行增删改查切换等</p></blockquote><div class="language-bash ext-sh"><pre class="shiki" style="background-color:#272822;"><code><span class="line"><span style="color:#88846F;"># 查看所有的分支和远程分支</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git branch -a</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 创建一个新的分支</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git branch [branch-name]</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 重命名分支</span></span>\n<span class="line"><span style="color:#88846F;"># git branch -m &lt;旧名称&gt; &lt;新名称&gt;</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git branch -m [branch-name] [new-branch-name]</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 编辑分支的介绍</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git branch [branch-name] --edit-description</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 列出所有本地分支</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git branch</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 列出所有远程分支</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git branch -r</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 新建一个分支，但依然停留在当前分支</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git branch [branch-name]</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 新建一个分支，并切换到该分支</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git checkout -b [branch]</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 新建一个分支，指向指定commit</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git branch [branch] [commit]</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 新建一个分支，与指定的远程分支建立追踪关系</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git branch --track [branch] [remote-branch]</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 切换到指定分支，并更新工作区</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git checkout [branch-name]</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 切换到上一个分支</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git checkout -</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 建立追踪关系，在现有分支与指定的远程分支之间</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git branch --set-upstream [branch] [remote-branch]</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 合并指定分支到当前分支</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git merge [branch]</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 选择一个commit，合并进当前分支</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git cherry-pick [commit]</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 删除分支</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git branch -d [branch-name]</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 删除远程分支</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git push origin --delete [branch-name]</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git branch -dr [remote/branch]</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 切换到某个分支</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git co </span><span style="color:#F92672;">&lt;</span><span style="color:#F8F8F2;">branch</span><span style="color:#F92672;">&gt;</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 创建新的分支，并且切换过去</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git co -b </span><span style="color:#F92672;">&lt;</span><span style="color:#F8F8F2;">new_branch</span><span style="color:#F92672;">&gt;</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 基于branch创建新的new_branch</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git co -b </span><span style="color:#F92672;">&lt;</span><span style="color:#F8F8F2;">new_branch</span><span style="color:#F92672;">&gt;</span><span style="color:#F8F8F2;"> </span><span style="color:#F92672;">&lt;</span><span style="color:#F8F8F2;">branch</span><span style="color:#F92672;">&gt;</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 把某次历史提交记录checkout出来，但无分支信息，切换到其他分支会自动删除</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git co $id</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 把某次历史提交记录checkout出来，创建成一个分支</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git co $id -b </span><span style="color:#F92672;">&lt;</span><span style="color:#F8F8F2;">new_branch</span><span style="color:#F92672;">&gt;</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 删除某个分支</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git branch -d </span><span style="color:#F92672;">&lt;</span><span style="color:#F8F8F2;">branch</span><span style="color:#F92672;">&gt;</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 强制删除某个分支 (未被合并的分支被删除的时候需要强制)</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git branch -D </span><span style="color:#F92672;">&lt;</span><span style="color:#F8F8F2;">branch</span><span style="color:#F92672;">&gt;</span></span>\n<span class="line"></span></code></pre></div><h3 id="检出" tabindex="-1"><a class="header-anchor" href="#检出" aria-hidden="true">#</a> 检出</h3><blockquote><p>将当前工作空间更新到索引所标识的或者某一特定的工作空间</p></blockquote><div class="language-bash ext-sh"><pre class="shiki" style="background-color:#272822;"><code><span class="line"><span style="color:#88846F;"># 检出一个版本库，默认将更新到master分支</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git checkout</span></span>\n<span class="line"><span style="color:#88846F;"># 检出到一个特定的分支</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git checkout branchName</span></span>\n<span class="line"><span style="color:#88846F;"># 新建一个分支，并且切换过去，相当于&quot;git branch &lt;名字&gt;; git checkout &lt;名字&gt;&quot;</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git checkout -b newBranch</span></span>\n<span class="line"></span></code></pre></div><h3 id="远程同步" tabindex="-1"><a class="header-anchor" href="#远程同步" aria-hidden="true">#</a> 远程同步</h3><blockquote><p>远程同步的远端分支</p></blockquote><div class="language-bash ext-sh"><pre class="shiki" style="background-color:#272822;"><code><span class="line"><span style="color:#88846F;"># 下载远程仓库的所有变动</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git fetch [remote]</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 显示所有远程仓库</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git remote -v</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 显示某个远程仓库的信息</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git remote show [remote]</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 增加一个新的远程仓库，并命名</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git remote add [shortname] [url]</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 查看远程服务器地址和仓库名称</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git remote -v</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 添加远程仓库地址</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git remote add origin git@ github:xxx/xxx.git</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 设置远程仓库地址(用于修改远程仓库地址)</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git remote set-url origin git@ github.com:xxx/xxx.git</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 删除远程仓库</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git remote rm </span><span style="color:#F92672;">&lt;</span><span style="color:#F8F8F2;">repository</span><span style="color:#F92672;">&gt;</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 上传本地指定分支到远程仓库</span></span>\n<span class="line"><span style="color:#88846F;"># 把本地的分支更新到远端origin的master分支上</span></span>\n<span class="line"><span style="color:#88846F;"># git push &lt;远端&gt; &lt;分支&gt;</span></span>\n<span class="line"><span style="color:#88846F;"># git push 相当于 git push origin master</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git push [remote] [branch]</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 强行推送当前分支到远程仓库，即使有冲突</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git push [remote] --force</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 推送所有分支到远程仓库</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git push [remote] --all</span></span>\n<span class="line"></span></code></pre></div><h3 id="撤销" tabindex="-1"><a class="header-anchor" href="#撤销" aria-hidden="true">#</a> 撤销</h3><div class="language-bash ext-sh"><pre class="shiki" style="background-color:#272822;"><code><span class="line"><span style="color:#88846F;"># 恢复暂存区的指定文件到工作区</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git checkout [file]</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 恢复某个commit的指定文件到暂存区和工作区</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git checkout [commit] [file]</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 恢复暂存区的所有文件到工作区</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git checkout </span><span style="color:#66D9EF;">.</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 重置暂存区的指定文件，与上一次commit保持一致，但工作区不变</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git reset [file]</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 重置暂存区与工作区，与上一次commit保持一致</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git reset --hard</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 重置当前分支的指针为指定commit，同时重置暂存区，但工作区不变</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git reset [commit]</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 重置当前分支的HEAD为指定commit，同时重置暂存区和工作区，与指定commit一致</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git reset --hard [commit]</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 重置当前HEAD为指定commit，但保持暂存区和工作区不变</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git reset --keep [commit]</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 新建一个commit，用来撤销指定commit</span></span>\n<span class="line"><span style="color:#88846F;"># 后者的所有变化都将被前者抵消，并且应用到当前分支</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git revert [commit]</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 恢复最后一次提交的状态</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git revert HEAD</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 暂时将未提交的变化移除，稍后再移入</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git stash</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git stash pop</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 列所有stash</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git stash list</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 恢复暂存的内容</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git stash apply</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 删除暂存区</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git stash drop</span></span>\n<span class="line"></span></code></pre></div><h3 id="commit" tabindex="-1"><a class="header-anchor" href="#commit" aria-hidden="true">#</a> commit</h3><blockquote><p>将当前索引的更改保存为一个新的提交，这个提交包括用户做出的更改与信息</p></blockquote><div class="language-bash ext-sh"><pre class="shiki" style="background-color:#272822;"><code><span class="line"><span style="color:#88846F;"># 提交暂存区到仓库区附带提交信息</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git commit -m [message]</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 提交暂存区的指定文件到仓库区</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git commit [file1] [file2] ... -m [message]</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 提交工作区自上次commit之后的变化，直接到仓库区</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git commit -a</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 提交时显示所有diff信息</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git commit -v</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 使用一次新的commit，替代上一次提交</span></span>\n<span class="line"><span style="color:#88846F;"># 如果代码没有任何新变化，则用来改写上一次commit的提交信息</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git commit --amend -m [message]</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 重做上一次commit，并包括指定文件的新变化</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git commit --amend [file1] [file2] ...</span></span>\n<span class="line"></span></code></pre></div><h3 id="diff" tabindex="-1"><a class="header-anchor" href="#diff" aria-hidden="true">#</a> diff</h3><blockquote><p>显示当前工作空间和提交的不同</p></blockquote><div class="language-bash ext-sh"><pre class="shiki" style="background-color:#272822;"><code><span class="line"><span style="color:#88846F;"># 显示工作目录和索引的不同</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git diff</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 显示索引和最近一次提交的不同</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git diff --cached</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 显示工作目录和最近一次提交的不同</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git diff HEAD</span></span>\n<span class="line"></span></code></pre></div><h3 id="grep" tabindex="-1"><a class="header-anchor" href="#grep" aria-hidden="true">#</a> grep</h3><blockquote><p>可以在版本库中快速查找</p></blockquote><p>可选配置：</p><div class="language-bash ext-sh"><pre class="shiki" style="background-color:#272822;"><code><span class="line"><span style="color:#88846F;"># 感谢Travis Jeffery提供的以下用法：</span></span>\n<span class="line"><span style="color:#88846F;"># 在搜索结果中显示行号</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git config --global grep.lineNumber </span><span style="color:#66D9EF;">true</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 是搜索结果可读性更好</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git config --global alias.g </span><span style="color:#E6DB74;">&quot;grep --break --heading --line-number&quot;</span></span>\n<span class="line"></span></code></pre></div><div class="language-bash ext-sh"><pre class="shiki" style="background-color:#272822;"><code><span class="line"><span style="color:#88846F;"># 在所有的java中查找variableName</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git grep </span><span style="color:#E6DB74;">&#39;variableName&#39;</span><span style="color:#F8F8F2;"> -- </span><span style="color:#E6DB74;">&#39;*.java&#39;</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 搜索包含 &quot;arrayListName&quot; 和, &quot;add&quot; 或 &quot;remove&quot; 的所有行</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git grep -e </span><span style="color:#E6DB74;">&#39;arrayListName&#39;</span><span style="color:#F8F8F2;"> --and </span><span style="color:#AE81FF;">\\(</span><span style="color:#F8F8F2;"> -e add -e remove </span><span style="color:#AE81FF;">\\)</span></span>\n<span class="line"></span></code></pre></div><h3 id="log" tabindex="-1"><a class="header-anchor" href="#log" aria-hidden="true">#</a> log</h3><blockquote><p>显示这个版本库的所有提交</p></blockquote><div class="language-bash ext-sh"><pre class="shiki" style="background-color:#272822;"><code><span class="line"><span style="color:#88846F;"># 显示所有提交</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git log</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 显示某几条提交信息</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git log -n 10</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 仅显示合并提交</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git log --merges</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 查看该文件每次提交记录</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git log </span><span style="color:#F92672;">&lt;</span><span style="color:#F8F8F2;">file</span><span style="color:#F92672;">&gt;</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 查看每次详细修改内容的diff</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git log -p </span><span style="color:#F92672;">&lt;</span><span style="color:#F8F8F2;">file</span><span style="color:#F92672;">&gt;</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 查看最近两次详细修改内容的diff</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git log -p -2</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;">#查看提交统计信息</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git log --stat</span></span>\n<span class="line"></span></code></pre></div><h3 id="merge" tabindex="-1"><a class="header-anchor" href="#merge" aria-hidden="true">#</a> merge</h3><blockquote><p>将外部的提交合并到自己的分支中</p></blockquote><div class="language-bash ext-sh"><pre class="shiki" style="background-color:#272822;"><code><span class="line"><span style="color:#88846F;"># 将其他分支合并到当前分支</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git merge branchName</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 在合并时创建一个新的合并后的提交</span></span>\n<span class="line"><span style="color:#88846F;"># 不要 Fast-Foward 合并，这样可以生成 merge 提交</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git merge --no-ff branchName</span></span>\n<span class="line"></span></code></pre></div><h3 id="mv" tabindex="-1"><a class="header-anchor" href="#mv" aria-hidden="true">#</a> mv</h3><blockquote><p>重命名或移动一个文件</p></blockquote><div class="language-bash ext-sh"><pre class="shiki" style="background-color:#272822;"><code><span class="line"><span style="color:#88846F;"># 重命名</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git mv test.js test2.js</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 移动</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git mv test.js ./new/path/test.js</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 改名文件，并且将这个改名放入暂存区</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git mv [file-original] [file-renamed]</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 强制重命名或移动</span></span>\n<span class="line"><span style="color:#88846F;"># 这个文件已经存在，将要覆盖掉</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git mv -f myFile existingFile</span></span>\n<span class="line"></span></code></pre></div><h3 id="tag" tabindex="-1"><a class="header-anchor" href="#tag" aria-hidden="true">#</a> tag</h3><div class="language-bash ext-sh"><pre class="shiki" style="background-color:#272822;"><code><span class="line"><span style="color:#88846F;"># 列出所有tag</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git tag</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 新建一个tag在当前commit</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git tag [tag]</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 新建一个tag在指定commit</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git tag [tag] [commit]</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 删除本地tag</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git tag -d [tag]</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 删除远程tag</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git push origin :refs/tags/[tagName]</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 查看tag信息</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git show [tag]</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 提交指定tag</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git push [remote] [tag]</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 提交所有tag</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git push [remote] --tags</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 新建一个分支，指向某个tag</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git checkout -b [branch] [tag]</span></span>\n<span class="line"></span></code></pre></div><h3 id="pull" tabindex="-1"><a class="header-anchor" href="#pull" aria-hidden="true">#</a> pull</h3><blockquote><p>从远端版本库合并到当前分支</p></blockquote><div class="language-bash ext-sh"><pre class="shiki" style="background-color:#272822;"><code><span class="line"><span style="color:#88846F;"># 从远端origin的master分支更新版本库</span></span>\n<span class="line"><span style="color:#88846F;"># git pull &lt;远端&gt; &lt;分支&gt;</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git pull origin master</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 抓取远程仓库所有分支更新并合并到本地，不要快进合并</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git pull --no-ff</span></span>\n<span class="line"></span></code></pre></div><h3 id="ci" tabindex="-1"><a class="header-anchor" href="#ci" aria-hidden="true">#</a> ci</h3><div class="language-bash ext-sh"><pre class="shiki" style="background-color:#272822;"><code><span class="line"><span style="color:#F8F8F2;">$ git ci </span><span style="color:#F92672;">&lt;</span><span style="color:#F8F8F2;">file</span><span style="color:#F92672;">&gt;</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git ci </span><span style="color:#66D9EF;">.</span></span>\n<span class="line"><span style="color:#88846F;"># 将git add, git rm和git ci等操作都合并在一起做</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git ci -a</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git ci -am </span><span style="color:#E6DB74;">&quot;some comments&quot;</span></span>\n<span class="line"><span style="color:#88846F;"># 修改最后一次提交记录</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git ci --amend</span></span>\n<span class="line"></span></code></pre></div><h3 id="rebase-谨慎使用" tabindex="-1"><a class="header-anchor" href="#rebase-谨慎使用" aria-hidden="true">#</a> rebase (谨慎使用)</h3><blockquote><p>将一个分支上所有的提交历史都应用到另一个分支上不要在一个已经公开的远端分支上使用 rebase.</p></blockquote><div class="language-bash ext-sh"><pre class="shiki" style="background-color:#272822;"><code><span class="line"><span style="color:#88846F;"># 将experimentBranch应用到master上面</span></span>\n<span class="line"><span style="color:#88846F;"># git rebase &lt;basebranch&gt; &lt;topicbranch&gt;</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git rebase master experimentBranch</span></span>\n<span class="line"></span></code></pre></div><h3 id="reset-谨慎使用" tabindex="-1"><a class="header-anchor" href="#reset-谨慎使用" aria-hidden="true">#</a> reset (谨慎使用)</h3><blockquote><p>将当前的头指针复位到一个特定的状态。这样可以使你撤销 merge、pull、commits、add 等 这是个很强大的命令，但是在使用时一定要清楚其所产生的后果</p></blockquote><div class="language-bash ext-sh"><pre class="shiki" style="background-color:#272822;"><code><span class="line"><span style="color:#88846F;"># 使 staging 区域恢复到上次提交时的状态，不改变现在的工作目录</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git reset</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 使 staging 区域恢复到上次提交时的状态，覆盖现在的工作目录</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git reset --hard</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 将当前分支恢复到某次提交，不改变现在的工作目录</span></span>\n<span class="line"><span style="color:#88846F;"># 在工作目录中所有的改变仍然存在</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git reset dha78as</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 将当前分支恢复到某次提交，覆盖现在的工作目录</span></span>\n<span class="line"><span style="color:#88846F;"># 并且删除所有未提交的改变和指定提交之后的所有提交</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git reset --hard dha78as</span></span>\n<span class="line"></span></code></pre></div><h3 id="其他" tabindex="-1"><a class="header-anchor" href="#其他" aria-hidden="true">#</a> 其他</h3><div class="language-bash ext-sh"><pre class="shiki" style="background-color:#272822;"><code><span class="line"><span style="color:#88846F;"># 生成一个可供发布的压缩包</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git archive</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 打补丁</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git apply ../sync.patch</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 测试补丁能否成功</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git apply --check ../sync.patch</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#88846F;"># 查看Git的版本</span></span>\n<span class="line"><span style="color:#F8F8F2;">$ git --version</span></span>\n<span class="line"></span></code></pre></div>',69);n.render=function(s,n){return a};export default n;
